function niftyplayer(e){this.obj=FlashHelper.getMovie(e);if(!FlashHelper.movieIsLoaded(this.obj))return;this.play=function(){this.obj.TCallLabel("/","play")};this.stop=function(){this.obj.TCallLabel("/","stop")};this.pause=function(){this.obj.TCallLabel("/","pause")};this.playToggle=function(){this.obj.TCallLabel("/","playToggle")};this.reset=function(){this.obj.TCallLabel("/","reset")};this.load=function(e){this.obj.SetVariable("currentSong",e);this.obj.TCallLabel("/","load")};this.loadAndPlay=function(e){this.load(e);this.play()};this.getState=function(){var e=this.obj.GetVariable("playingState");var t=this.obj.GetVariable("loadingState");if(e=="playing")if(t=="loaded")return e;else return t;if(e=="stopped")if(t=="empty")return t;if(t=="error")return t;else return e;return e};this.getPlayingState=function(){return this.obj.GetVariable("playingState")};this.getLoadingState=function(){return this.obj.GetVariable("loadingState")};this.registerEvent=function(e,t){this.obj.SetVariable(e,t)};return this}var FlashHelper={movieIsLoaded:function(e){if(typeof e!="undefined")return e.PercentLoaded()==100;else return;false},getMovie:function(e){if(navigator.appName.indexOf("Microsoft")!=-1)return window[e];else return document[e]}}